mapper.select:
select

  distinct

'true' as QUERYID,

from comm_user_info

  


  order by ${orderByClause}


	limit  #{skip,jdbcType=INTEGER},#{rows,jdbcType=INTEGER}

    select t.id, t.unique_code, t.login_code, t.password, t.real_name, t.sex_code, t.birthday, t.user_type, 
    t.user_unit, a.name unit_name, t.mavin_code, (select a.name from comm_mavin_info a where a.unique_code = t.mavin_code) mavin_name, 
    t.address, t.phone, t.email, t.is_super, t.im, t.id_card, t.qq, t.wechat_code, 
    t.msn, t.search_code, t.last_login_date, t.last_login_ip, t.image_url, t.status, t.remark, t.creator, 
    t.create_time, t.update_time, t.version
    from comm_user_info t join comm_center_info a on a.unique_code = t.user_unit
   	
   		t.is_super !='1' 
   		
   			and ((t.login_code like #{name,jdbcType=VARCHAR}) or (t.real_name like #{name,jdbcType=VARCHAR}) or (t.search_code like #{name,jdbcType=VARCHAR}))
   		
   		
   			and a.name like #{organName,jdbcType=VARCHAR}
   		
   		
   			and a.area_code like #{areaCode,jdbcType=VARCHAR}
   		
   		
   			and t.user_type = #{userType,jdbcType=CHAR}
   		
   		
   			and t.status = #{status,jdbcType=CHAR}
   		
   		
   			and a.unique_code = #{centerCode,jdbcType=VARCHAR}
   		
   	
    
    	
    		order by #{sort} #{order}
    	
    
    
	  	
	    	limit  #{skip} , #{rows}

    select count(1) from comm_user_info t join comm_center_info a on a.unique_code = t.user_unit
   	
   		t.is_super !='1' 
   		
   			and ((t.login_code like #{name,jdbcType=VARCHAR}) or (t.real_name like #{name,jdbcType=VARCHAR}) or (t.search_code like #{name,jdbcType=VARCHAR}))
   		
   		
   			and a.name like #{organName,jdbcType=VARCHAR}
   		
   		
   			and a.area_code like #{areaCode,jdbcType=VARCHAR}
   		
   		
   			and t.user_type = #{userType,jdbcType=CHAR}
   		
   		
   			and t.status = #{status,jdbcType=CHAR}
   		
   		
   			and a.unique_code = #{centerCode,jdbcType=VARCHAR}
   		
   	
    
    	
    		order by #{sort} #{order}
    	
    
    
	  	
	    	limit  #{skip} , #{rows}

    select t.id, t.unique_code, t.login_code, t.password, t.real_name, t.sex_code, t.birthday, t.user_type, 
    t.user_unit, a.name unit_name, t.mavin_code, (select a.name from comm_mavin_info a where a.unique_code = t.mavin_code) mavin_name, 
    t.address, t.phone, t.email, t.is_super, t.im, t.id_card, t.qq, t.wechat_code, 
    t.msn, t.search_code, t.last_login_date, t.last_login_ip, t.image_url, t.status, t.remark, t.creator, 
    t.create_time, t.update_time, t.version
    from comm_user_info t join comm_organ_info a on a.unique_code = t.user_unit
   	
   		t.is_super !='1' 
   		
   			and ((t.login_code like #{name,jdbcType=VARCHAR}) or (t.real_name like #{name,jdbcType=VARCHAR}) or (t.search_code like #{name,jdbcType=VARCHAR}))
   		
   		
   			and a.name like #{organName,jdbcType=VARCHAR}
   		
   		
   			and a.area_code like #{areaCode,jdbcType=VARCHAR}
   		
   		
   			and t.user_type = #{userType,jdbcType=CHAR}
   		
   		
   			and t.status = #{status,jdbcType=CHAR}
   		
   	
    
    	
    		order by #{sort} #{order}
    	
    
    
	  	
	    	limit  #{skip} , #{rows}

    select count(1) from comm_user_info t join comm_organ_info a on a.unique_code = t.user_unit
   	
   		t.is_super !='1' 
   		
   			and ((t.login_code like #{name,jdbcType=VARCHAR}) or (t.real_name like #{name,jdbcType=VARCHAR}) or (t.search_code like #{name,jdbcType=VARCHAR}))
   		
   		
   			and a.name like #{organName,jdbcType=VARCHAR}
   		
   		
   			and a.area_code like #{areaCode,jdbcType=VARCHAR}
   		
   		
   			and t.user_type = #{userType,jdbcType=CHAR}
   		
   		
   			and t.status = #{status,jdbcType=CHAR}
   		
   	
    
    	
    		order by #{sort} #{order}
    	
    
    
	  	
	    	limit  #{skip} , #{rows}
